import React, { Component } from 'react';
import Proptypes from 'prop-types';
import { connect } from 'react-redux';
import './table.css';
import { deleteFromList } from '../redux/actions';

class Table extends Component {
  findCoinName = (expense) => {
    const name = Object.values(expense.exchangeRates).filter(
      (each) => each.code === expense.currency,
    );
    return name[0].name;
  };

  exchangeUsed = (expense) => {
    const exchange = Object.values(expense.exchangeRates).filter(
      (a) => a.code === expense.currency,
    )[0].ask;

    return Number(exchange).toFixed(2);
  };

  render() {
    const { expenses, dispatch } = this.props;

    return (
      <table>
        <thead>
          <tr>
            <th>Descrição</th>
            <th>Tag</th>
            <th>Método de pagamento</th>
            <th>Valor</th>
            <th>Moeda</th>
            <th>Câmbio utilizado</th>
            <th>Valor convertido</th>
            <th>Moeda de conversão</th>
            <th>Editar/Excluir</th>
          </tr>
        </thead>
        <tbody>
          {expenses.length < 1
            ? null
            : expenses.map((expense) => (
              <tr key={ expense.id }>
                <td key={ expense.id }>{expense.description}</td>
                <td>{expense.tag}</td>
                <td>{expense.method}</td>
                <td>{Number(expense.value).toFixed(2)}</td>
                <td>{this.findCoinName(expense)}</td>
                <td>{this.exchangeUsed(expense)}</td>
                <td>
                  {(
                    Object.values(expense.exchangeRates).filter(
                      (a) => a.code === expense.currency,
                    )[0].ask * expense.value
                  ).toFixed(2)}
                </td>
                <td>Real</td>
                <td>
                  <button
                    onClick={ (event) => console.log(event.target) }
                    type="button"
                    data-testid="delete-btn"
                  >
                    Excluir
                  </button>
                </td>
              </tr>
            ))}
        </tbody>
      </table>
    );
  }
}

Table.propTypes = {
  email: Proptypes.array,
}.isRequired;

const mapStateToProps = (globalState) => ({
  expenses: globalState.wallet.expenses,
});

export default connect(mapStateToProps)(Table);
