import React, { Component } from 'react';
import Proptypes from 'prop-types';
import { connect } from 'react-redux';
import './table.css';

class Table extends Component {
  findCoinName = (expense) => {
    const name = Object.values(expense.exchangeRates).filter(
      (each) => each.code === expense.currency,
    );
    return name[0].name;
  };

  sumExpenses = (expense) => {
    let totalExpenses = 0;


      console.log(expense.exchangeRates)
      // const currencies = expense.exchangeRates;
      // const values = Object.values(currencies);
      // const valuesReturn = values.filter((t) => t.code === expense.currency);
      // const { ask } = valuesReturn;
      // totalExpenses = expense.value * ask;
      // return totalExpenses.toFixed(2);

  };

  render() {
    const { expenses } = this.props;

    return (
      <table>
        <tbody>
          <tr>
            <th>Descrição</th>
            <th>Tag</th>
            <th>Método de pagamento</th>
            <th>Valor</th>
            <th>Moeda</th>
            <th>Câmbio utilizado</th>
            <th>Valor convertido</th>
            <th>Moeda de conversão</th>
            <th>Editar/Excluir</th>
          </tr>
          {expenses.length < 1
            ? null
            : expenses.map((expense) => (
              <tr key={ expense.id }>
                <td>{expense.description}</td>
                <td>{expense.tag}</td>
                <td>{expense.method}</td>
                <td>{expense.value}</td>
                <td>{this.findCoinName(expense)}</td>
                <td>{expense.currency}</td>
                <td>{this.sumExpenses(expense)}</td>
                <td>Real</td>
              </tr>
            ))}
          {/* <tr>
          <td>Centro comercial Moctezuma</td>
          <td>Francisco Chang</td>
          <td>Mexico</td>
        </tr> */}
        </tbody>
      </table>
    );
  }
}

Table.propTypes = {
  email: Proptypes.array,
}.isRequired;

const mapStateToProps = (globalState) => ({
  expenses: globalState.wallet.expenses,
});

export default connect(mapStateToProps)(Table);
