import React, { Component } from 'react';
import Proptypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchCurrencies, saveForm } from '../redux/actions';
import getFullCurrencyApi from '../services/FullCurrencyAPI';

class WalletForm extends Component {
  state = {
    id: 0,
    value: '',
    description: '',
    currency: 'USD',
    method: 'dinheiro',
    tag: 'Alimentação',
  };

  componentDidMount() {
    const { dispatch } = this.props;
    dispatch(fetchCurrencies());
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  handleSubmit = async (event) => {
    event.preventDefault();
    const currencyAPI = await getFullCurrencyApi();
    const { dispatch } = this.props;
    const { id, value, description, currency, method, tag } = this.state;
    dispatch(saveForm({
      id,
      value,
      description,
      currency,
      method,
      tag,
      exchangeRates: currencyAPI,
    }));
    this.setState({
      id: id + 1,
    });
    this.handleReset();
  };

  handleReset = () => {
    this.setState({
      value: '',
      description: '',
      currency: 'USD',
      method: 'dinheiro',
      tag: 'Alimentação',
    });
  };

  editInfos = () => {
    console.log('oi');
  };

  render() {
    const { currencies, edit } = this.props;
    const { description, currency, value, method, tag } = this.state;
    return (
      <div>
        <form onSubmit={ this.handleSubmit }>
          <label htmlFor="number">
            Valor:
            <input
              onChange={ this.handleChange }
              name="value"
              data-testid="value-input"
              id="number"
              type="number"
              value={ value }
            />
          </label>
          <label htmlFor="description">
            Descrição:
            <input
              onChange={ this.handleChange }
              name="description"
              data-testid="description-input"
              id="description"
              type="text"
              value={ description }
            />
          </label>

          <label htmlFor="currency">
            Moeda:
            <select
              onChange={ this.handleChange }
              data-testid="currency-input"
              name="currency"
              id="currency"
              value={ currency }
            >
              {currencies.map((currenc) => (
                <option key={ currenc }>{currenc}</option>
              ))}
            </select>
          </label>

          <label htmlFor="paymentMethod">
            Método de pagamento:
            <select
              onChange={ this.handleChange }
              data-testid="method-input"
              name="method"
              id="paymentMethod"
              value={ method }
            >
              <option>Dinheiro</option>
              <option>Cartão de crédito</option>
              <option>Cartão de débito</option>
            </select>
          </label>

          <label htmlFor="category">
            Categoria:
            <select
              onChange={ this.handleChange }
              data-testid="tag-input"
              name="tag"
              id="category"
              value={ tag }
            >
              <option>Alimentação</option>
              <option>Lazer</option>
              <option>Trabalho</option>
              <option>Transporte</option>
              <option>Saúde</option>
            </select>
          </label>
          {edit ? (
            <button type="button" onClick={ this.editInfos() }>Editar despesa</button>
          ) : (
            <button type="submit">Adicionar despesa</button>
          )}
        </form>
      </div>
    );
  }
}

WalletForm.propTypes = {
  email: Proptypes.array,
}.isRequired;

const mapStateToProps = (globalState) => ({
  currencies: globalState.wallet.currencies,
  edit: globalState.wallet.edit,
});

export default connect(mapStateToProps)(WalletForm);
