import React, { Component } from 'react';
import Proptypes from 'prop-types';
import { connect } from 'react-redux';

const totalExpenses = 0;
class Header extends Component {
  sumExpenses = () => {
    const { expensetosum } = this.props;

    if (expensetosum.length > 0) {
      const currencies = expensetosum[0].exchangeRates;
      const values = Object.values(currencies);

      return values.filter((t) => t.code === expensetosum[0].currency);
    }
  };

  render() {
    const { email, expenses, expensetosum } = this.props;
    console.log(this.sumExpenses());

    return (
      <div>
        <h4 data-testid="total-field">
          Desspesa total:
          { expenses.length === 0 ? totalExpenses : null }
          {}
        </h4>
        <span data-testid="header-currency-field">BRL</span>
        <h3 data-testid="email-field">{ email }</h3>
      </div>
    );
  }
}

Header.propTypes = {
  email: Proptypes.string,
}.isRequired;

const mapStateToProps = (globalState) => ({
  email: globalState.user.email,
  expenses: globalState.wallet.expenses,
  expensetosum: globalState.wallet.expensetosum,
});

export default connect(mapStateToProps)(Header);
