import React, { Component } from 'react';
import { connect } from 'react-redux';
import Proptypes from 'prop-types';
import { saveLogedEmail } from '../redux/actions';

class Login extends Component {
  state = {
    email: [],
    emaill: '',
    password: '',
    isButtonDisable: true,
  };

  handleChange = (event) => {
    const passwordLength = 5;
    this.setState({
      [event.target.name]: event.target.value,
    }, () => {
      const { password, emaill } = this.state;
      this.setState({
        isButtonDisable:
          password.length <= passwordLength
          || !(emaill.includes('@') && emaill.toLocaleLowerCase().includes('.com')),
      });
    });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    const { emaill, email } = this.state;
    const { dispatch, history } = this.props;
    dispatch(saveLogedEmail(emaill));
    this.setState({
      email: [...email, emaill],
    })
    // history.push('/carteira');
  };

  render() {
    const { isButtonDisable } = this.state;
    return (
      <div>
        <form onSubmit={ this.handleSubmit }>
          <input
            onChange={ this.handleChange }
            data-testid="email-input"
            placeholder="insira seu email"
            type="text"
            name="emaill"
          />
          <input
            onChange={ this.handleChange }
            data-testid="password-input"
            placeholder="insira sua senha"
            type="password"
            name="password"
          />
          <button disabled={ isButtonDisable } type="submit">Entrar</button>
        </form>
      </div>
    );
  }
}

Login.propTypes = {
  dispatch: Proptypes.func,
}.isRequired;

export default connect()(Login);
