import React, { Component } from 'react';
import Proptypes from 'prop-types';
import { connect } from 'react-redux';
import './table.css';

class Table extends Component {

  state = {
    cad: 0,
  }

  findCoinName = (expense) => {
    const name = Object.values(expense.exchangeRates).filter(
      (s) => s.code === expense.currency,
    );
    return name[0].name;
  };

  sumExpenses = () => {
    let totalExpenses = 0;
    const { expensetosum } = this.props;
    const {cad} = this.state;

    if (expensetosum.length > 0) {
      const currencies = expensetosum[0].exchangeRates;
      const values = Object.values(currencies);
      const valuesReturn = values.filter((t) => t.code === expensetosum[0].currency);
      const { ask } = valuesReturn[0];
      this.setState({
        cad: expensetosum[0].value * ask,
      });
    }
  };

  render() {
    const { expenses } = this.props;
    const { cad } = this.state;

    return (
      <table>
        <tbody>
          <tr>
            <th>Descrição</th>
            <th>Tag</th>
            <th>Método de pagamento</th>
            <th>Valor</th>
            <th>Moeda</th>
            <th>Câmbio utilizado</th>
            <th>Valor convertido</th>
            <th>Moeda de conversão</th>
            <th>Editar/Excluir</th>
          </tr>
          {expenses.length < 1
            ? null
            : expenses.map((expense) => (
              <tr key={ expense.id }>
                <td>{expense.description}</td>
                <td>{expense.tag}</td>
                <td>{expense.method}</td>
                <td>{expense.value}</td>
                <td>{this.findCoinName(expense)}</td>
                <td>{expense.value}</td>
                <td>{cad}</td>
                <td>Real</td>
              </tr>
            ))}
          {/* <tr>
          <td>Centro comercial Moctezuma</td>
          <td>Francisco Chang</td>
          <td>Mexico</td>
        </tr> */}
        </tbody>
      </table>
    );
  }
}

Table.propTypes = {
  email: Proptypes.array,
}.isRequired;

const mapStateToProps = (globalState) => ({
  expenses: globalState.wallet.expenses,
  expensetosum: globalState.wallet.expensetosum,
});

export default connect(mapStateToProps)(Table);
