import React, { Component } from 'react';
import Proptypes from 'prop-types';
import { connect } from 'react-redux';
import getCurrencyApi from '../services/CURRENCYapi';

class WalletForm extends Component {
  render() {
    const { currencies } = this.props;
    return (
      <div>
        <label htmlFor="number">
          Valor:
          <input data-testid="value-input" id="number" type="number" />
        </label>
        <label htmlFor="description">
          Descrição:
          <input data-testid="description-input" id="description" type="text" />
        </label>

        <label htmlFor="currency">
          Moeda:
          <select data-testid="currency-input" name="currency" id="currency">
            {currencies.map((currency, index) => (
              <option key={ index } value="currency">
                {currency}
              </option>
            ))}
          </select>
        </label>

        <label htmlFor="paymentMethod">
          Método de pagamento:
          <select data-testid="method-input" name="method" id="paymentMethod">
            <option value="money">Dinheiro</option>
            <option value="creditCard">Cartão de crédito</option>
            <option value="debtCard">Cartão de débito</option>
          </select>
        </label>

        <label htmlFor="category">
          Categoria:
          <select data-testid="tag-input" name="category" id="category">
            <option value="food">Alimentação</option>
            <option value="leisure">Lazer</option>
            <option value="job">Trabalho</option>
            <option value="transport">Transporte</option>
            <option value="health">Saúde</option>
          </select>
        </label>
      </div>
    );
  }
}

WalletForm.propTypes = {
  email: Proptypes.array,
}.isRequired;

const mapStateToProps = (globalState) => ({
  currencies: globalState.wallet.currencies,
});

export default connect(mapStateToProps)(WalletForm);
