import React, { Component } from 'react';
import Proptypes from 'prop-types';
import { connect } from 'react-redux';

// let totalExpenses = 0;

class Header extends Component {
  sumExpenses = () => {
    const { expensetosum, dispatch } = this.props;

    if (expensetosum.length > 0) {
      const currencies = expensetosum[0].exchangeRates;
      const values = Object.values(currencies);
      const valuesReturn = values.filter((t) => t.code === expensetosum[0].currency);
      const { ask } = valuesReturn[0];
     const totalExpenses = expensetosum[0].value * ask;
      // dispatch('ADD_EXPENSE', totalExpenses);
      console.log(totalExpenses)
    }
  };

  render() {
    const { email, expenses } = this.props;
    return (
      <div>
        <h4 data-testid="total-field">
          { expenses.length === 0 ? totalExpenses : this.sumExpenses() }
        </h4>
        <span data-testid="header-currency-field">BRL</span>
        <h3 data-testid="email-field">{ email }</h3>
      </div>
    );
  }
}

Header.propTypes = {
  email: Proptypes.string,
}.isRequired;

const mapStateToProps = (globalState) => ({
  email: globalState.user.email,
  expenses: globalState.wallet.expenses,
  expensetosum: globalState.wallet.expensetosum,
});

export default connect(mapStateToProps)(Header);
