import React, { Component } from 'react';
import Proptypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchCurrencies } from '../redux/actions';

class WalletForm extends Component {

  state = {
    id: 0,
    value: '',
    description: '',
    currency: '',
    method: '',
    tag: '',
  }

  componentDidMount() {
    const { dispatch } = this.props;
    dispatch(fetchCurrencies());
  }

  handleChange = (event) => {
    console.log(event.target)
    this.setState({
      [event.target.name]: event.target.selected,
    });
  };

  render() {
    const { currencies } = this.props;
    return (
      <div>
        <form>
          <label htmlFor="number">
            Valor:
            <input onChange={ this.handleChange } name="value" data-testid="value-input" id="number" type="number" />
          </label>
          <label htmlFor="description">
            Descrição:
            <input onChange={ this.handleChange } name="description" data-testid="description-input" id="description" type="text" />
          </label>

          <label htmlFor="currency">
            Moeda:
            <select onChange={ this.handleChange } data-testid="currency-input" name="currency" id="currency">
              {currencies.map((currency) => (
                <option key={ currency } value="currency">
                  { currency}
                </option>
              ))}
            </select>
          </label>

          <label htmlFor="paymentMethod">
            Método de pagamento:
            <select onChange={ this.handleChange } data-testid="method-input" name="method" id="paymentMethod">
              <option value="money">Dinheiro</option>
              <option value="creditCard">Cartão de crédito</option>
              <option value="debtCard">Cartão de débito</option>
            </select>
          </label>

          <label htmlFor="category">
            Categoria:
            <select onChange={ this.handleChange } data-testid="tag-input" name="tag" id="category">
              <option value="food">Alimentação</option>
              <option value="leisure">Lazer</option>
              <option value="job">Trabalho</option>
              <option value="transport">Transporte</option>
              <option value="health">Saúde</option>
            </select>
          </label>
          <button type="button">Adicionar despesa</button>
        </form>
      </div>
    );
  }
}

WalletForm.propTypes = {
  email: Proptypes.array,
}.isRequired;

const mapStateToProps = (globalState) => ({
  currencies: globalState.wallet.currencies,
});

export default connect(mapStateToProps)(WalletForm);
